<script>
    function showchat(){
                id = 1;
                $.ajax({
                    type:"POST", url:"ajax.php", data:"cmd=show&one="+id, success:function(data){
                        //$("#chat").html(data);
                        //document.getElementById("msg").scrollIntoView();
                        $("#chat").html(function(){
                            return data;
                        });
                    }
                });
            }
    
    function send(){
        msg = $("#msg").val();
        id = 1;
        $.ajax({
                    type:"POST", url:"ajax.php", data:"cmd=send&one="+id+"&two="+msg, success:function(data){
                        $("#chat").html(data);
                        console.log("data = "+data);
                        showchat();
                        $("#msg").val("")
                        
                    }
                });
                
        
    }    
     var jml= 57;
    function checkmsg(){
        //jml=$("#jml").val();
        id = 1;
        
        $.ajax({
                    type:"POST", url:"ajax.php", data:"cmd=checkmsg&one="+id, success:function(data){
                        //console.log("id = "+id);
                        //console.log("data = "+data);
                        if(data!=jml){
                            console.log("data = "+data);
                            console.log(typeof data);
                            console.log("-------------------")
                            console.log("jml = "+jml);
                            console.log(typeof jml);
                            console.log("===================")
                            showchat();
                            //$("#jml").val(data)
                            jml = data; 
                        }
                    }
                });
    }
    
    function vote(id){
                
                $.ajax({
                    type:"POST", url:"ajax.php", data:"cmd=vote&one="+id, success:function(data){
                        //$("#chat").html(data);
                        //document.getElementById("msg").scrollIntoView();
                        showchat();
                        console.log(data);
                    }
                });
            }
    $(document).ready(function(){
        
        var intervalID = window.setInterval(checkmsg, 582);
    
        showchat();
    });
</script>

